import React,{ useState } from 'react'
// import data from './data.json';
// import ToDoList from "./ToDoList";
import ToDo from "./ToDo";
import ToDoForm from './ToDoForm';
import DropWrapper from "./DropWrapper";
import {Container,Row,Col} from 'react-bootstrap';
import { data, statuses } from "./data/index.js";
import './ToDoStyle.css';

const Title = ({status}) => {
  const [items, setItems] = useState(data);
  const addTask = (userInput ) => {
          let copy = [...items];
          copy = [...copy, { 
              id: items.length + 1,
              status: items.staus,
              title: userInput }];
          setItems(copy);
        }

  const onDrop = (item, monitor, status) => {
      const mapping = statuses.find(si => si.status === status);

      setItems(prevState => {
          const newItems = prevState
              .filter(i => i.id !== item.id)
              .concat({ ...item, status, icon: mapping.icon });
          return [ ...newItems ];
      });
  };

  const moveItem = (dragIndex, hoverIndex) => {
      const item = items[dragIndex];
      setItems(prevState => {
          const newItems = prevState.filter((i, idx) => idx !== dragIndex);
          newItems.splice(hoverIndex, 0, item);
          return  [ ...newItems ];
      });
  };

  return (
      <>
          {statuses.map(s => {
              return (
                         <> 
                         <Container>
      <Row>
                          <div key={s.status}></div>
        <h1 style={{color:"white",textAlign:"center"}}>{s.status}</h1>
        <div style={{backgroundColor:"white",borderRadius: "21px",padding:"20px"}}>
        <DropWrapper onDrop={onDrop} status={s.status}>
                          <Col >
                              {items
                                  .filter(i => i.status === s.status)
                                  .map((i, idx) => <ToDo key={i.id} item={i} index={idx} moveItem={moveItem} status={s} />)
                              }
                              <ToDoForm addTask={addTask}/>
                          </Col>
                      </DropWrapper>
        </div>
        </Row>
        </Container>
        </>
              );
          })}
          </>

  )};

export default Title;

