import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});
const AddButton = withStyles((theme) => ({
    root: {
        color: '#DA4848',   
        backgroundColor: '#FFFFFF',
        '&:hover': {
            backgroundColor: '#FCDADA',
        },
    },
}))(Button);

export default function SimpleCard() {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;

  return (
    <Card className={classes.root} style={{background: "#F1F2FC"}}>
      <CardContent>
        <TextField
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        size="small"
                        name="name"
                        label="Name of the board"
                        type="text"
                        id="text"
                        floatingLabelStyle={{ backgroundColor: '#FFFFFF' }}
                        className="box"
                        style={{borderRadius: 15}}
                    />
      <AddButton variant="contained" color="primary" className={classes.submit} fullWidth style={{borderRadius: 14}}>
                        Change Background
                    </AddButton>
      
      </CardContent>
      <CardActions>
        <Button size="small">Learn More</Button>
      </CardActions>
    </Card>
  );
}
